// Auto-generate 1000+ stories
function generateMoreStories() {
    const categories = ['stories', 'articles'];
    const types = [
        'Buddhist Story', 'Thirukkural', 'Siddhar Wisdom', 'Spiritual Story', 
        'Motivation', 'Bhagavad Gita', 'Tamil Saint', 'Greek Philosophy', 
        'Christian Wisdom', 'Sufi Story', 'Zen Story', 'Advaita Philosophy'
    ];
    const authors = [
        'புத்தர்', 'திருவள்ளுவர்', 'அகத்தியர்', 'ரமண மகரிஷி', 
        'விவேகானந்தர்', 'கிருஷ்ணர்', 'தாயுமானவர்', 'சாக்ரடீஸ்',
        'திருமூலர்', 'ஏசு', 'பட்டினத்தார்', 'காந்தி'
    ];
    
    for (let i = 11; i <= 1000; i++) {
        const randomCategory = categories[Math.floor(Math.random() * categories.length)];
        const randomType = types[Math.floor(Math.random() * types.length)];
        const randomAuthor = authors[Math.floor(Math.random() * authors.length)];
        const randomViews = Math.floor(Math.random() * 10000) + 1000;
        const randomDate = new Date(2026, Math.floor(Math.random() * 2), Math.floor(Math.random() * 28) + 1);
        
        storiesData.push({
            id: `s${i}`,
            title: `${randomType} - பகுதி ${i}`,
            excerpt: `இது ஒரு அற்புதமான ${randomType}. இதில் நீங்கள் பல உண்மைகளை அறியலாம். ${randomAuthor} கூறிய ஞான வார்த்தைகள்...`,
            content: `முழு கதை... இது ${i}-வது கதை. ${randomAuthor} கூறிய ஞான வார்த்தைகள் நமக்கு வழிகாட்டுகின்றன. வாழ்க்கையில் பல உண்மைகளை உணர உதவுகின்றன. தினமும் ஒரு கதை படிப்பதால் மனம் அமைதி அடைகிறது.`,
            date: randomDate.toISOString().split('T')[0],
            category: randomCategory,
            image: `https://picsum.photos/600/400?random=${300 + i}`,
            author: randomAuthor,
            type: randomType,
            views: randomViews
        });
    }
}

// Call this function to generate 1000+ stories
generateMoreStories();

// Auto RSS Feed Simulation
function startRSSFeed() {
    setInterval(() => {
        const newStory = {
            id: `s${storiesData.length + 1}`,
            title: `புதிய கதை - ${new Date().toLocaleDateString('ta-IN')}`,
            excerpt: `இன்று புதிதாக சேர்க்கப்பட்ட கதை. தினமும் 10+ புதிய கதைகள்...`,
            content: `இன்றைய சிறப்பு கதை... தினமும் புதிய கதைகளை நீங்கள் படிக்கலாம்.`,
            date: new Date().toISOString().split('T')[0],
            category: Math.random() > 0.5 ? 'stories' : 'articles',
            image: `https://picsum.photos/600/400?random=${400 + storiesData.length}`,
            author: 'ஆதியும் அந்தமும்',
            type: 'Daily Story'
        };
        storiesData.unshift(newStory); // Add to beginning
        
        // Keep only last 1000 stories to manage memory
        if (storiesData.length > 1000) {
            storiesData.pop();
        }
        
        // Update UI if on stories page
        const storiesGrid = document.getElementById('storiesGrid');
        if (storiesGrid) {
            loadStories();
        }
    }, 3600000); // Add new story every hour
}

// Call this when page loads
setTimeout(startRSSFeed, 5000);